func Test冒泡(t *testing.T) {
	a := 冒泡排序(array)
	for i := 0; i < len(a) - 1; i++ {
		if a[i] > a[i + 1] {
			t.Error("冒泡排序出错...")
		}
	}
	
	fmt.Println(a)
}

func Test选择(t *testing.T) {
	a := 选择排序(array)
	for i := 0; i < len(a) - 1; i++ {
		if a[i] > a[i + 1] {
			t.Error("选择排序出错...")
		}
	}
	
	fmt.Println(a)
}

func Benchmark冒泡(b*testing.B) {
	for i := 0; i < b.N; i++ {
		冒泡排序(array)
	}
}

func Benchmark选择(b*testing.B) {
	for i := 0; i < b.N; i++ {
		选择排序(array)
	}
}

